var packageJson = require('./package.json')
var deps = Object.keys(packageJson.dependencies)
var externals = {}
if(deps.length > 0) deps.forEach(dep => externals[dep] = dep)

module.exports = {
	name: 'gulp-bufferify',
	type: 'npm',
	build: [
		{
			from: 'src/gulp-bufferify.js',
			to: 'dist/gulp-bufferify.js',
			driver: 'webpack',
			options: {
				minify: false,
				sourcemap: false,
			},
			settings: {
				output: {
					library: 'gulp-bufferify',
					libraryTarget: 'commonjs2',
				},
				target: 'node',
				node: {
					global: false,
					Buffer: false,
				},
				externals: externals,
			},
		}
	],
	test: {
		entry: 'test/gulp-bufferify.js',
		browsers: 'Terminal',
	},
}
